import React, { useState, useEffect } from "react";
import Layout from "./Layout";
import {
  FaUsers,
  FaBox,
  FaReceipt,
  FaUserShield,
  FaArrowUp,
  FaArrowDown,
  FaEye,
} from "react-icons/fa";
import { userAPI, productAPI, transactionAPI, adminAPI } from "../services/api";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import i18n from "i18next";

const Dashboard = () => {
  const { t } = useTranslation();
  const [stats, setStats] = useState({
    users: { total: 0, change: 0 },
    products: { total: 0, change: 0 },
    transactions: { total: 0, change: 0 },
    admins: { total: 0, change: 0 },
  });
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      const [usersRes, productsRes, transactionsRes, adminsRes] = await Promise.all([
        userAPI.getAll(),
        productAPI.getAll(),
        transactionAPI.getAll(),
        adminAPI.getAll(),
      ]);

      setStats({
        users: { total: usersRes?.data?.length || 0, change: 12 },
        products: { total: productsRes?.data?.length || 0, change: 8 },
        transactions: { total: transactionsRes?.data?.length || 0, change: -5 },
        admins: { total: adminsRes?.data?.length || 0, change: 3 },
      });

      setRecentTransactions(transactionsRes?.data?.slice(0, 5) || []);
    } catch (error) {
      toast.error(t("dashboardPage.errorLoadingData"));
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ title, value, change, icon: Icon, color, colorEnd }) => (
    <div
      className="stat-card"
      style={{
        "--card-color": color,
        "--card-color-end": colorEnd || color,
      }}
    >
      <div className="stat-header">
        <div className="stat-info">
          <h3 className="stat-title">{title}</h3>
          <p className="stat-value">{loading ? "..." : (value || 0).toLocaleString()}</p>
        </div>
        <div className="stat-icon">
          <Icon />
        </div>
      </div>
      <div className="stat-footer">
        <div className={`stat-change ${(change || 0) >= 0 ? "positive" : "negative"}`}>
          {(change || 0) >= 0 ? <FaArrowUp /> : <FaArrowDown />}
          <span>{Math.abs(change || 0)}%</span>
        </div>
        <span className="stat-period">{t("dashboardPage.vsLastMonth")}</span>
      </div>
    </div>
  );

  if (loading) {
    return (
      <Layout>
        <div className="loading">{t("dashboardPage.loading")}</div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="dashboard">
        {/* Language Switcher */}
        <div className="lang-switcher" style={{ textAlign: "right", marginBottom: "10px" }}>
          <button
            onClick={() => i18n.changeLanguage("en")}
            className="btn btn-outline-primary btn-sm"
            style={{ marginRight: "5px" }}
          >
            English
          </button>
          <button
            onClick={() => i18n.changeLanguage("ar")}
            className="btn btn-outline-secondary btn-sm"
          >
            عربي
          </button>
        </div>

        {/* Stats */}
        <div className="stats-grid">
          <StatCard
            title={t("dashboardPage.totalUsers")}
            value={stats.users.total}
            change={stats.users.change}
            icon={FaUsers}
            color="#3b82f6"
            colorEnd="#1d4ed8"
          />
          <StatCard
            title={t("dashboardPage.totalProducts")}
            value={stats.products.total}
            change={stats.products.change}
            icon={FaBox}
            color="#f59e0b"
            colorEnd="#d97706"
          />
          <StatCard
            title={t("dashboardPage.totalTransactions")}
            value={stats.transactions.total}
            change={stats.transactions.change}
            icon={FaReceipt}
            color="#8b5cf6"
            colorEnd="#7c3aed"
          />
          <StatCard
            title={t("dashboardPage.totalAdmins")}
            value={stats.admins.total}
            change={stats.admins.change}
            icon={FaUserShield}
            color="#10b981"
            colorEnd="#059669"
          />
        </div>

        {/* Recent Transactions */}
        <div className="dashboard-section">
          <div className="card">
            <div className="card-header">
              <h2 className="card-title">{t("dashboardPage.recentTransactions")}</h2>
              <a href="/transactions" className="btn btn-primary btn-sm">
                <FaEye /> {t("dashboardPage.viewAll")}
              </a>
            </div>

            {loading ? (
              <div className="loading">{t("dashboardPage.loading")}</div>
            ) : recentTransactions.length > 0 ? (
              <div className="table-responsive">
                <table className="table">
                  <thead>
                    <tr>
                      <th>{t("Transaction ID")}</th>
                      <th>{t("User")}</th>
                      <th>{t("Amount")}</th>
                      <th>{t("Status")}</th>
                      <th>{t("Date")}</th>
                    </tr>
                  </thead>
                  <tbody>
                    {recentTransactions.map((transaction) => (
                      <tr key={transaction._id || transaction.id}>
                        <td>#{transaction._id || transaction.id}</td>
                        <td>{transaction.userName || "N/A"}</td>
                        <td>${transaction.amount?.toFixed(2) || "0.00"}</td>
                        <td>
                          <span className={`status-badge ${transaction.status?.toLowerCase()}`}>
                            {transaction.status || "Pending"}
                          </span>
                        </td>
                        <td>
                          {transaction.createdAt
                            ? new Date(transaction.createdAt).toLocaleDateString()
                            : "N/A"}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="empty-state">
                <FaReceipt />
                <p>{t("dashboardPage.noRecentTransactions")}</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </Layout>
  );
};

export default Dashboard;
